import logo from '../logo_3.png';
import fullLogo from '../full_logo.png';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams
} from "react-router-dom";
import { useEffect, useState } from 'react';
import { useLocation } from 'react-router';

function Navbar() {

  const [connected, toggleConnect] = useState(false);
  const location = useLocation();
  const [currAddress, updateAddress] = useState('0x');

  async function getAddress(){
    const ethers = require("ethers");
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const addr = await signer.getAddress();
    updateAddress(addr);
  }

  function updateButton(){
    const ethereumButton = document.querySelector(".enableEthereumButton");
    ethereumButton.textContent = "";
    ethereumButton.classList.remove("hover:bg-red-70");
    ethereumButton.classList.remove("bg-red-500");
    ethereumButton.classList.add("hover:bg-green-70");
    ethereumButton.classList.add("bg-green-500");
    ethereumButton.classList.add("bg-green-70");
    ethereumButton.classList.add("hover:bg-green-70");
  }

  async function connectWebsite(){
    const chainId = await window.ethereum.request({method: "eth_chainId"});
    if(chainId !== "0x5"){
      await window.ethereum.request({
        method: "wallet_switchEthereumChain",
        params: [{chainId: "0x5"}]
      })
    }
    await window.ethereum.request({method: "eth_requestAccounts"})
      .then(()=>{
        updateButton();
        console.log("here");
        getAddress();
        // window.location.href = "/";
      });
  }

  useEffect(()=>{
    let val = window.ethereum.isConnected();
    if(val){
      console.log("is it because of this?", val);
      getAddress();
      toggleConnect(val);
      updateButton();
    }
    window.ethereum.on("accountsChanged", function(accounts){
      window.location.href = "/";
    });
  });

    return (
      <div className="">
        <nav className="w-screen">
          <ul className='flex items-end justify-between py-3 bg-transparent text-white pr-5'>
          <li className='flex items-end ml-5 pb-2'>
            <Link to="/">
            <img src={fullLogo} alt="" width={120} height={120} className="inline-block -mt-2"/>
            <div className='inline-block font-bold text-xl ml-2'>
              NFT Marketplace
            </div>
            </Link>
          </li>
          <li className='w-2/6'>
            <ul className='lg:flex justify-between font-bold mr-10 text-lg'>
              {location.pathname === "/" ? 
              <li className='border-b-2 hover:pb-0 p-2'>
                <Link to="/">Marketplace</Link>
              </li>
              :
              <li className='hover:border-b-2 hover:pb-0 p-2'>
                <Link to="/">Marketplace</Link>
              </li>              
              }
              {location.pathname === "/sellNFT" ? 
              <li className='border-b-2 hover:pb-0 p-2'>
                <Link to="/sellNFT">List My NFT</Link>
              </li>
              :
              <li className='hover:border-b-2 hover:pb-0 p-2'>
                <Link to="/sellNFT">List My NFT</Link>
              </li>              
              }              
              {location.pathname === "/profile" ? 
              <li className='border-b-2 hover:pb-0 p-2'>
                <Link to="/profile">Profile</Link>
              </li>
              :
              <li className='hover:border-b-2 hover:pb-0 p-2'>
                <Link to="/profile">Profile</Link>
              </li>              
              }  
              <li>
                <button className="enableEthereumButton bg-green-70 hover:bg-green-500 text-white font-bold py-2 px-4 rounded text-sm"
                  onClick={connectWebsite}
                >{window.ethereum.isConnected ? "Connected!":"Connect Wallet"}</button>
              </li>
            </ul>
          </li>
          </ul>
        </nav>
        <div className='text-white text-bold text-right mr-10 text-sm'>
          {currAddress !== "0x" ? "Connected to":"Not Connected. Please login to view NFTs"} {currAddress !== "0x" ? (currAddress.substring(0,15)+'...'):""}
        </div>
      </div>
    );
  }

  export default Navbar;
